import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom'; // To get userId from URL

const AddBookToCartPage = () => {
  const [books, setBooks] = useState([]);
  const [selectedBookId, setSelectedBookId] = useState('');
  const [quantity, setQuantity] = useState(1);
  const { userId } = useParams(); // Assume userId is passed as a URL parameter

  useEffect(() => {
    // Fetch available books
    axios.get('/books')
      .then(response => setBooks(response.data))
      .catch(error => console.error('Error fetching books:', error));
  }, []);

  const handleAddToCart = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`/carts/${userId}/books/${selectedBookId}`, null, {
        params: {
          quantity: quantity,
        },
      });
      alert('Book added to cart successfully!');
    } catch (error) {
      console.error('Error adding book to cart:', error);
      alert('Error adding book to cart.');
    }
  };

  return (
    <div>
      <h2>Add Book to Cart</h2>
      <form onSubmit={handleAddToCart}>
        <div>
          <label htmlFor="book">Select Book:</label>
          <select
            id="book"
            value={selectedBookId}
            onChange={(e) => setSelectedBookId(e.target.value)}
          >
            <option value="">Select a book</option>
            {books.map(book => (
              <option key={book.id} value={book.id}>
                {book.title}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="number"
            id="quantity"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
            min="1"
          />
        </div>
        <button type="submit">Add to Cart</button>
      </form>
    </div>
  );
};


export default AddBookToCartPage;
